const jsonPrettify = (json) => {
    if (typeof json === "object" && json !== null) {
        return JSON.stringify(json, undefined, '\t')
    }
    
    try {
        const obj = JSON.parse(json)
        return jsonPrettify(obj)
    } catch (e) {
        return json
    }
}

